>\n\n<!-- wp:paragraph -->\n<p>MASHINASOZLIK FAKULTЕTI</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Mashinasozlik texnologiyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Yer usti transport tizimlari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Xizmat ko’rsatish texnikasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Energiya mashinasozligi va kasb ta’limi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Chizma geometriya va kompyuter grafikasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>6&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Oliy matematika</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MЕXANIKA FAKULTЕTI</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Materialshunoslik</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Metallarga bosim bilan ishlov berish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Quymakorlik texnologiyalari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sovutish va kriogen texnika</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Texnologik mashinalar va jihozlar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>6&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nazariy mexanika va mashina va mexanizmlar nazariyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>7&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Materiallar qarshiligi va mashina detallari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>8&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Umumiy fizika</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>IJTIMOIY-GUMANITAR FANLAR MARKAZI</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; O’zbekiston tarixi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Falsafa va milliy q’oya</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Pedagogika va psixologiya</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sanoat iqtisodiyoti</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Jismoniy tarbiya</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>6&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tillar kafedrasi (o’zbek va rus)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>7&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Chet tillari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>FAKULTЕTLARARO KAFЕDRA</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Harbiy kafedra</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>b) TDTUda faoliyat ko’rsatayotgan o’quv-ilmiy amaliy majmualar:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Islom Karimov nomidagi Toshkent davlat texnika universiteti&nbsp; Termiz filiali</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Islom Karimov nomidagi Toshkent davlat texnika universiteti&nbsp; Olmaliq filiali</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Islom Karimov nomidagi Toshkent davlat texnika universiteti&nbsp; Qo’qon filiali</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; «Brichmulla» o’quv- geologik poligoni</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>v) TDTU qoshidagi ilmiy Markazlar:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; “Fan va taraqqiyot” Davlat unitar korxonasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; O’zbekiston-Yaponiya yoshlar innovatsion markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Pedagog kadrlarni qayta tayyorlash va ularni malakasini oshirish&nbsp; &nbsp; tarmoq markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tarmoqlararo Strategik Innovatsiya va Informatizatsiya markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; “Energiya va resurslarni tejash” ilmiy-amaliy va o’quv markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>g) TDTUda faoliyat ko’rsatayotgan ilmiy laboratoriyalar:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Energiyatejamkor texnologiyalar, nazorat o’lchov va avtomatlashtirilgan tizimlar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Foydali&nbsp; qazilma konlarini ochiq usulda qazib olish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Foydali qazilmalar konlari geologiyasi, izlash va qidirish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Konchilik&nbsp; elektr mexanikasi muammolari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Geomexanika va gedeziya</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>6&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ekologiya va atrof-muhit muhofazasi, hayot faoliyati havfsizligi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>7&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Termodinamik jarayonlar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>8&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sanoat korxonalarining elektr ta’minoti tizimlarini energiya samaradorligi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>9&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Elektruzatma</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>10&nbsp; &nbsp; &nbsp; &nbsp; Energiya samarador qurilmalar va tizimlar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>11&nbsp; &nbsp; &nbsp; &nbsp; Energiya tejamkorlik&nbsp; va qayta tiklanuvchan energiya manbalari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>12&nbsp; &nbsp; &nbsp; &nbsp; Metallarga mexanik ishlov berish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>13&nbsp; &nbsp; &nbsp; &nbsp; Materialshunoslik va yangi materiallar texnologiyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>14&nbsp; &nbsp; &nbsp; &nbsp; Materialshunoslik va kompozitsion materiallar texnologiyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>15&nbsp; &nbsp; &nbsp; &nbsp; Mexanika</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>16&nbsp; &nbsp; &nbsp; &nbsp; Qishloq xo’jalik mahsulotlarini qayta ishlash texnologiyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>17&nbsp; &nbsp; &nbsp; &nbsp; Sovutish mashinalari va issiqlik nasoslari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>18&nbsp; &nbsp; &nbsp; &nbsp; Metrologiya va sifatni boshqarish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>19&nbsp; &nbsp; &nbsp; &nbsp; Texnologik ishlab chiqarish va jarayonlarni modellashtirish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>20&nbsp; &nbsp; &nbsp; &nbsp; Elektronika va noan’anaviy energiya manbalari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>21&nbsp; &nbsp; &nbsp; &nbsp; Lazer, Ion va plazma texnologiyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>22&nbsp; &nbsp; &nbsp; &nbsp; Ko’p fazali tizimlar issiqlik fizikasi, elektron va ion jarayonlari texnologiyasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>23&nbsp; &nbsp; &nbsp; &nbsp; Tibbiy biologik tizimlar va jihozlar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>24&nbsp; &nbsp; &nbsp; &nbsp; Amaliy elektronika va istiqbolli qurilmalar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>25&nbsp; &nbsp; &nbsp; &nbsp; Optika. Spektroskopiya va lazer texnologiyalari</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>26&nbsp; &nbsp; &nbsp; &nbsp; Loyiha konstruktorlik-texnologik ishlanmalar va tajriba ishlab chiqarish</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>TDTU ta’sischiligida tashkil etilgan ilmiy markaz hamda kichik korxonalar:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; “Fan va taraqqiyot” Davlat unitar korxonasi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; “O’zbekiston -Yaponiya Yoshlarning innovatsiya Markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Strategik innovatsiyalar va informatsiyalar Markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; “Quduqlarni burq’ilash jarayonidagi asoratlarga qarshi kurash” ilmiy markazi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; “BURGUCHI BIZNES” o’quv-ilmiy ishlab chiqarish korxonasi</p>\n<!-- /wp:paragraph -->', 'INISTITUT NIZOMI', '', 'inherit', 'closed', 'closed', '', '127-revision-v1', '', '', '2024-04-30 13:59:45', '2024-04-30 10:59:45', '', 127, 'http://localhost/wp/?p=128', 0, 'revision', '', 0),
